import Layout from '../components/Layout';
import PostItem from '../components/PostItem';
import { getAllPosts, getAllTILs } from '../../lib/api';
import TitleLabel from '../components/TitleLabel';
import TILItem from '../components/TILItem';

function TIL({ allTILs }) {
  return (
    <Layout>
      <TitleLabel className="mt-16">Recent Writing:</TitleLabel>
      <div className="my-8">
        {allTILs.length > 0 &&
          allTILs.map((til) => <TILItem til={til} key={til.slug} />)}
      </div>
    </Layout>
  );
}

export default TIL;

export async function getStaticProps() {
  const allTILs = getAllTILs(['title', 'date', 'slug', 'isPublished']);
  return {
    props: { allTILs },
  };
}
