import Link from 'next/link';
import { useRouter } from 'next/router';

function Header() {
  return (
    <>
      <nav className="transform -skew-x-6 flex justify-between h-16 border-2 px-4 my-2 border-teal-900 items-center">
        <Link href="/">
          <a className="text-red">Logo</a>
        </Link>
        <ul className="flex items-center">
          <NavLink href="/writing" className="mr-6">
            Writing
          </NavLink>
          <NavLink href="/about">About</NavLink>
        </ul>
      </nav>
    </>
  );
}

function NavLink(props) {
  const router = useRouter();
  const { href, className, children, ...rest } = props;

  return (
    <Link href={href}>
      <li
        className={`${
          router.pathname === href ? 'text-teal-900' : 'text-black'
        } hover:text-teal-900 cursor-pointer ${className}`}
        {...rest}
      >
        {children}
      </li>
    </Link>
  );
}

export default Header;
