import Link from 'next/link';

function PostItem({ title, date, className, href }) {
  return (
    <div className={className}>
      <div className="w-sm border p-4">
        <div className="flex justify-between items-center flex-wrap ">
          <div className="mb-4 sm:mb-0">
            <Link href={href}>
              <a className="font-medium">{title}</a>
            </Link>
            <div className="text-gray-600">{date}</div>
          </div>
          <div className="flex items-center">
            {['React', 'Javascript'].map((v) => (
              <div className="border border-teal border-2 px-2 py-1 rounded-full mr-2 text-sm bg-teal-200">
                {v}
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default PostItem;
