import Layout from '../components/Layout';
import Profile from '../components/Profile';
import PostItem from '../components/PostItem';
import { getAllPosts } from '../../lib/api';

export default function HomePage({ allPosts }) {
  return (
    <Layout>
      <div className="mx-auto">
        <Profile />
        <div className="flex justify-between items-start">
          <div>
            <div className="text-gray-600 font-bold">Recent Writing:</div>
            <div className="mt-8">
              {allPosts.length > 0 &&
                allPosts.map((post) => (
                  <PostItem post={post} key={post.slug} />
                ))}
            </div>
          </div>
          <div className="hidden md:inline-block flex-1 flex-col ">
            <div className="text-center text-gray-600 font-bold">
              Portfolio:
            </div>
            <div className="px-4 py-8 flex flex-col items-center justify-center">
              <a
                href="https://tituy-client.now.sh/"
                target="_blank"
                rel="noopener"
              >
                <div
                  className="flex flex-col items-center justify-center w-64 h-64 border rounded-md bg-gray-100 "
                  style={{
                    backgroundImage: "url('/images/tituy.png')",
                    objectFit: 'contain',
                  }}
                >
                  <div className="font-bold pb-4 text-xl text-primary">
                    Tituy
                  </div>
                  <div className="text-gray-800 px-4">
                    Reading, Writing, Sharing online platform for Cambodian
                    people.
                  </div>
                </div>
              </a>
            </div>
          </div>
        </div>
      </div>
    </Layout>
  );
}

export async function getStaticProps() {
  const allPosts = getAllPosts(['title', 'date', 'slug']);
  return {
    props: { allPosts },
  };
}
