import Layout from '../components/Layout';
import Logo from '../components/SVG/Logo';
import { useState, useEffect } from 'react';

function About() {
  const [loading, setLoading] = useState(true);
  useEffect(() => {
    if (loading) {
      setTimeout(() => setLoading(false), 1500);
    }
  }, [loading]);

  return (
    <Layout>
      <div className="mt-64 flex justify-center items-center">
        {loading && <Logo className="h-64 w-64" />}
        {!loading && (
          <div className="flex flex-col">
            <div>Hi, I'm Tepy, from Cambodia ðŸ‡°ðŸ‡­</div>
            <div>
              I came to Japan in 2015 to pursue my undergraduate study in
              Computer Science with a full scholarship.
            </div>
            <div className="block h-8"> </div>
            <div>
              Back in Cambodia in 2012, I was majored in Civil Engineering since
              I didn't know what I really wanted to do back then. After studying
              for almost 2 years, I found myself having more interest in
              programming than civil engineering.
            </div>
            <div className="mt-8">
              I started my first line of code in <span>Lua</span> while trying
              to make mobile game with my friends. I started out as I was really
              curious about how people make games or applications by that time,
              but eventually I got hooked into the world of programming without
              knowing it myself.
            </div>
          </div>
        )}
      </div>
    </Layout>
  );
}

export default About;
